sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		var rightnow = new GlideDateTime();
		var inputDate = new GlideDateTime(strDate);
		if(inputDate.before(rightnow)){
			return true;
		}
		else{
			return false;
		}
	},
	isDayToday: function(strDate){
		var today=new GlideDateTime().getLocalDate();
		var inputDay=new GlideDateTime(strDate).getLocalDate();
		if(inputDay.compareTo(today)==0){
			return true;
		}
		else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
